mapper.insert:
insert into notice (NOTICENO, NOTICETITLE, USERID, NOTICEDATE, NOTICE_UPFILE, NOTICE_REFILE, NOTICECONTENT, READCOUNT, IMPORTANCE) values ((select max(noticeno) + 1 from notice), #{ noticetitle }, #{ userid }, sysdate, #{ notice_upfile, jdbcType=VARCHAR }, #{ notice_refile, jdbcType=VARCHAR }, #{ noticecontent }, default ,
#{ importance }
default )

mapper.update:
update notice
set noticetitle = #{ noticetitle },
	noticecontent = #{ noticecontent}

	, notice_upfile = #{ notice_upfile }
	, notice_refile = #{ notice_refile }


	, notice_upfile = null
	, notice_refile = null



	, importance = #{ importance }


	, importance = default


where noticeno = #{ noticeno }

update notice
set readcount = readcount + 1
where noticeno = #{ noticeno }
